mutation registerOrUpdateDevice($deviceId: UUID, $input: DeviceInput!) {
  registerOrUpdateDevice(deviceId: $deviceId, device: $input) {
    __typename
    deviceId
    sentry {
      __typename
      env
      dsn
    }
  }
}

mutation CreateConversation($title: String, $providerIds: [UUID!], $initialMessage: SendMessageInput) {
  createConversation(
    title: $title
    providerIds: $providerIds
    initialMessage: $initialMessage
  ) {
    __typename
    conversation {
      __typename
      ...ConversationFragment
    }
  }
}
fragment ConversationFragment on Conversation {
  __typename
  id
  title
  subtitle
  lastMessagePreview
  lastMessage {
    __typename
    ...MessageFragment
  }
  unreadMessageCount
  inboxPreviewTitle
  updatedAt
  pictureUrl {
    __typename
    ...EphemeralUrlFragment
  }
  providers {
    __typename
    ...ProviderInConversationFragment
  }
  isLocked
}
fragment MessageFragment on Message {
  __typename
  id
  clientId
  createdAt
  author {
    __typename
    ...MessageAuthorFragment
  }
  content {
    __typename
    ...MessageContentFragment
  }
  replyTo {
    __typename
    ...ReplyMessageFragment
  }
}
fragment MessageAuthorFragment on MessageAuthor {
  __typename
  ... on Provider {
    ...ProviderFragment
  }
  ... on Patient {
    ...PatientFragment
  }
  ... on System {
    ...SystemMessageFragment
  }
  ... on DeletedProvider {
    empty: _
  }
}
fragment ProviderFragment on Provider {
  __typename
  id
  avatarUrl {
    __typename
    ...EphemeralUrlFragment
  }
  prefix
  firstName
  lastName
}
fragment EphemeralUrlFragment on EphemeralUrl {
  __typename
  expiresAt
  url
}
fragment PatientFragment on Patient {
  __typename
  id
  isMe
  displayName
}
fragment SystemMessageFragment on System {
  __typename
  avatar {
    __typename
    url {
      __typename
      ...EphemeralUrlFragment
    }
  }
  name
}
fragment MessageContentFragment on MessageContent {
  __typename
  ... on TextMessageContent {
    ...TextMessageContentFragment
  }
  ... on ImageMessageContent {
    ...ImageMessageContentFragment
  }
  ... on VideoMessageContent {
    ...VideoMessageContentFragment
  }
  ... on DocumentMessageContent {
    ...DocumentMessageContentFragment
  }
  ... on AudioMessageContent {
    ...AudioMessageContentFragment
  }
  ... on DeletedMessageContent {
    empty: _
  }
  ... on LivekitRoomMessageContent {
    ...LivekitRoomMessageContentFragment
  }
}
fragment TextMessageContentFragment on TextMessageContent {
  __typename
  text
}
fragment ImageMessageContentFragment on ImageMessageContent {
  __typename
  imageFileUpload {
    __typename
    id
    url {
      __typename
      url
    }
    fileName
    mimeType
    width
    height
  }
}
fragment VideoMessageContentFragment on VideoMessageContent {
  __typename
  videoFileUpload {
    __typename
    id
    url {
      __typename
      url
    }
    fileName
    mimeType
    width
    height
  }
}
fragment DocumentMessageContentFragment on DocumentMessageContent {
  __typename
  documentFileUpload {
    __typename
    id
    url {
      __typename
      url
    }
    fileName
    mimeType
    thumbnail {
      __typename
      url {
        __typename
        url
      }
    }
  }
}
fragment AudioMessageContentFragment on AudioMessageContent {
  __typename
  audioFileUpload {
    __typename
    id
    url {
      __typename
      ...EphemeralUrlFragment
    }
    fileName
    mimeType
    durationMs
  }
}
fragment LivekitRoomMessageContentFragment on LivekitRoomMessageContent {
  __typename
  livekitRoom {
    __typename
    ...LivekitRoomFragment
  }
}
fragment LivekitRoomFragment on LivekitRoom {
  __typename
  uuid
  status {
    __typename
    ... on LivekitRoomOpenStatus {
      ...LivekitRoomOpenStatusFragment
    }
    ... on LivekitRoomClosedStatus {
      ...LivekitRoomClosedStatusFragment
    }
  }
}
fragment LivekitRoomOpenStatusFragment on LivekitRoomOpenStatus {
  __typename
  url
  token
}
fragment LivekitRoomClosedStatusFragment on LivekitRoomClosedStatus {
  __typename
  _
}
fragment ReplyMessageFragment on Message {
  __typename
  id
  clientId
  createdAt
  author {
    __typename
    ...MessageAuthorFragment
  }
  content {
    __typename
    ...MessageContentFragment
  }
}
fragment ProviderInConversationFragment on ProviderInConversation {
  __typename
  id
  provider {
    __typename
    ...ProviderFragment
  }
  typingAt
  seenUntil
}
